SqlSettingsRepository::~SqlSettingsRepository()
{

}

void SqlSettingsRepository::LoadSettingsFile()
{
    const QSettings setting(m_iniFilePath, QSettings::IniFormat);
    m_host = setting.value(QStringLiteral("host"), QStringLiteral("localhost")).toString();
    m_port = setting.value(QStringLiteral("port"), 5432).toUInt();
    m_dbName = setting.value(QStringLiteral("dbName"), QStringLiteral("postgres")).toString();
    m_userName = setting.value(QStringLiteral("userName"), QStringLiteral("mikerov")).toString();
    m_userPassword = setting.value(QStringLiteral("userPassword"), QStringLiteral("user1234")).toString();
}

void SqlSettingsRepository::SaveSettingsFile()
{
    QSettings setting(m_iniFilePath, QSettings::IniFormat);
    setting.setValue("host", m_host);
    setting.setValue("port", m_port);
    setting.setValue("dbName", m_dbName);
    setting.setValue("userName", m_userName);
    setting.setValue("userPassword", m_userPassword);
    setting.sync();
}

const QString &SqlSettingsRepository::GetHost() const noexcept
{
    return m_host;
}

quint16 SqlSettingsRepository::GetPort() const noexcept
{
    return m_port;
}

const QString &SqlSettingsRepository::GetDbName() const noexcept
{
    return  m_dbName;
}

const QString &SqlSettingsRepository::GetUserName() const noexcept
{
    return m_userName;
}

const QString &SqlSettingsRepository::GetUserPassword() const noexcept
{
    return  m_userPassword;
}

void SqlSettingsRepository::CheckFields() noexcept
{
    for (const QString &sqlKeyword : m_sqlKeywords)
    {
        if (sqlKeyword == m_userName)
        {
            m_userName = "mikerov";
        }
        if (sqlKeyword == m_userPassword)
        {
            m_userPassword = "user1234";
        }
    }
}
